//! WASM bindings for DCAP quote parsing and verification.
//!
//! This module provides WebAssembly bindings for parsing Intel DCAP quotes and verified outputs.
//! It wraps the existing Rust utilities with WASM-friendly interfaces that serialize results to JavaScript.
//!
//! ## Features
//! - Parse verified outputs (version-aware, supporting v1.0 and v1.1)
//! - Parse quotes
//! - Locate quotes within transaction data
//!
//! Enable the `wasm` feature to use this module.

#![cfg(feature = "wasm")]

use core::panic;

use anyhow::{bail, Result};
use serde_wasm_bindgen;
use wasm_bindgen::prelude::*;

use crate::{parser, Version};

// =============================================================================
// WASM Bindings - Wrappers around existing utils functions
// =============================================================================

/// Parse verified output bytes with version awareness
///
/// This is a WASM wrapper around `parser::parse_output()` that supports both v1.0 and v1.1 formats.
///
/// # Arguments
/// * `output_bytes` - Raw verified output bytes
/// * `version_str` - Version indicator ("v1.0" or "v1.1")
///
/// # Returns
/// `true` if the output can be parsed successfully, `false` otherwise
#[wasm_bindgen]
pub fn parse_verified_output(output_bytes: &[u8], version_str: &str) -> JsValue {
    let version = version_str
        .parse::<Version>()
        .expect("Invalid version string");

    let output =
        parser::parse_output(output_bytes, version).expect("Failed to parse verified output");
    serde_wasm_bindgen::to_value(&output).unwrap()
}

