// =============================================================================
// Quote Location Functions
// =============================================================================

/// Locate a raw quote within transaction data and return it with metadata
///
/// # Arguments
/// * `txn_data` - Transaction data bytes to search
/// * `quote_version` - Quote version to search for (3 or 4)
/// * `idx` - Index of the quote to retrieve (if multiple matches found)
///
/// # Returns
/// Serialized `LocatedQuoteOutput` containing the quote, version, and raw bytes
#[wasm_bindgen]
pub fn locate_raw_quote_js(txn_data: &[u8], quote_version: u16, idx: usize) -> JsValue {
    let located_quote = locate_raw_quote(txn_data, quote_version, idx);
    match located_quote {
        Ok(quote_bytes) => serde_wasm_bindgen::to_value(&quote_bytes).unwrap(),
        Err(e) => {
            let error_msg = format!("Error locating quote: {}", e);
            panic!("{}", error_msg);
        }
    }
}

/// Get the number of possible quote locations within transaction data
///
/// # Arguments
/// * `txn_data` - Transaction data bytes to search
/// * `quote_version` - Quote version to search for (3 or 4)
///
/// # Returns
/// Number of possible quote locations found
#[wasm_bindgen]
pub fn locate_possible_quote_length_js(txn_data: &[u8], quote_version: u8) -> JsValue {
    let length = locate_possible_quote_length(txn_data, quote_version);
    serde_wasm_bindgen::to_value(&length).unwrap()
}

// =============================================================================
// Internal Helper Functions
// =============================================================================

/// Internal: Get the count of possible quote locations
pub fn locate_possible_quote_length(txn_data: &[u8], quote_version: u8) -> usize {
    let possible_quote_bytes_vec = find_possible_quote_bytes_vec(txn_data, quote_version);
    possible_quote_bytes_vec.len()
}

/// Internal: Locate and parse a raw quote from transaction data
pub fn locate_raw_quote(txn_data: &[u8], quote_version: u16, idx: usize) -> Result<Vec<u8>> {
    if quote_version != 3 && quote_version != 4 {
        bail!("Unsupported version: {}", quote_version)
    }

    let possible_quote_bytes_vec = find_possible_quote_bytes_vec(txn_data, quote_version as u8);
    if idx >= possible_quote_bytes_vec.len() {
        bail!("Index out of bounds: {}", idx);
    }

    // Parse the quote to get the structure and calculate its exact size
    let quote = parser::parse_quote(&possible_quote_bytes_vec[idx])?;

    // Calculate the exact byte length of the quote
    let quote_len = quote.byte_len();

    // Extract only the exact bytes that represent this quote
    // This ensures we don't include any trailing bytes that weren't part of the quote
    Ok(possible_quote_bytes_vec[idx][..quote_len].to_vec())
}

/// Internal: Find all possible quote byte sequences in transaction data
///
/// Searches for the version marker (version byte followed by 0x00) and returns
/// all byte sequences starting from each match.
pub fn find_possible_quote_bytes_vec(txn_data: &[u8], quote_version: u8) -> Vec<Vec<u8>> {
    let mut results: Vec<Vec<u8>> = Vec::new();
    for i in 0..txn_data.len() - 1 {
        if txn_data[i] == quote_version && txn_data[i + 1] == 0 {
            let sub_slice = txn_data[i..].to_vec();
            results.push(sub_slice);
        }
    }
    results
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_locate_raw_quote_v3() {
        let txn_data = hex::decode("").unwrap();
        let len = locate_possible_quote_length(&txn_data, 3);

        for idx in 0..len {
            let quote_bytes_parsed = locate_raw_quote(&txn_data, 3, idx);
            if quote_bytes_parsed.is_ok() {
                let quote_bytes = quote_bytes_parsed.unwrap();
                let quote = parser::parse_quote(&quote_bytes).unwrap();
                assert_eq!(quote.header.version.get(), 3);
                println!("Found v3 quote at index {}", idx);
                println!("{}", quote);
            } else {
                println!(
                    "Failed to parse quote at index {}: {}",
                    idx,
                    quote_bytes_parsed.err().unwrap()
                );
            }
        }
    }

    #[test]
    fn test_locate_raw_quote_v4() {
        let txn_data = hex::decode("").unwrap();
        let len = locate_possible_quote_length(&txn_data, 4);

        for idx in 0..len {
            let quote_bytes_parsed = locate_raw_quote(&txn_data, 4, idx);
            if quote_bytes_parsed.is_ok() {
                let quote_bytes = quote_bytes_parsed.unwrap();
                let quote = parser::parse_quote(&quote_bytes).unwrap();
                assert_eq!(quote.header.version.get(), 4);
                println!("Found v4 quote at index {}", idx);
                println!("{}", quote);
            } else {
                println!(
                    "Failed to parse quote at index {}: {}",
                    idx,
                    quote_bytes_parsed.err().unwrap()
                );
            }
        }
    }
}
